DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `GetRelatedRecipesByRecipe`(
	In ParamRecipeID int
	-- in ParamFoodTypeID int, 
	-- in ParamPriceRangeID int,
	-- in ParamEstPeopleID int,
	-- in ParamFoodProcessID int,
	-- in ParamCuisineID int
    )
BEGIN
	SELECT b.* FROM (
		SELECT a.*, 
		COUNT(fcmt.FoodCommentID) 'NumberOfComment'  FROM(
			SELECT r.RecipeID, r.RecipeName,r.PrimaryPhoto, COUNT(rck.RecookID) 'NumberOfRecook'
			from recipe r
			LEFT JOIN recook rck ON rck.RecipeID = r.RecipeID AND rck.AuditedActivity<>'D'
			WHERE 
			(	(r.RecipeID IN (SELECT RecipeID FROM foodcuisine WHERE 
				FIND_IN_SET(CuisineID, (SELECT GROUP_CONCAT(CuisineID) FROM FoodCuisine
				WHERE RecipeID=ParamRecipeID AND AuditedActivity<>'D')))) 
			OR FoodProcessID  = (SELECT FoodProcessID From Recipe WHERE RecipeID=ParamRecipeID)
			OR PriceRangeID = (SELECT PriceRangeID FROM Recipe WHERE RecipeID=ParamRecipeID)
			OR EstPeopleID=(SELECT EstPeopleID FROM Recipe WHERE RecipeID=ParamRecipeID)
			OR FoodTypeID =(SELECT FoodTypeID FROM Recipe WHERE RecipeID=ParamRecipeID)
			)
			AND r.AuditedActivity<>'D'
			GROUP BY r.RecipeID, r.RecipeName, r.PrimaryPhoto
		) as a
		LEFT JOIN foodcomment fcmt ON fcmt.RecipeID = a.RecipeID AND fcmt.AuditedActivity<>'D'
		GROUP BY a.RecipeID, a.RecipeName, a.PrimaryPhoto
	) as b
